import "./App.css";

// React front-end code for uploading an image
import React, { useState } from "react";
import axios from "axios";

function App() {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("file", file);

    //try {
    //   await
    axios
      .post("http://localhost:9090/api/upload", formData, {
        headers: {
          Authorization: `Bearer sl.BbWGjL1RhWu_ezzcId5_JTifQySeRE9RQvx6j2WcAzpRUhUjN5izD_fjh0J5vL723-gPWsroamjOUYZRVqiQGtsf56ZQD8nNty2EPf5cZqRZ5Yct6PZGZZKdkh-kbWMsuzL2rUB9`,
        },
      })
      .then((response) => {
        alert("File uploaded successfully");
        // Use the Dropbox API to retrieve the image URL
        const filePath = response.data.path_display;
        axios
          .post("http://localhost:9090/api/getImage", { filePath })
          .then((response) => {
            setImageUrl(response.data);
          });
      });
    //}
    // catch (error) {
    //   alert("Error uploading file");
    // }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}

export default App;
